//         await window.ethereum.send('eth_requestAccounts');
//         window.web3 = new Web3(window.ethereum);
//         return true;
//     }
//     return false;
//     }
//     const accounts = await web3.eth.getAccounts();
//     console.log("account if it is connecting or not ", accounts)
//     const account = await accounts[0]
//     if(typeof account != 'undefined'){
//         dispatch(web3AccountLoaded(account))
//         return account
//     } else {
//         window.alert('Please login with MetaMask ')
//         return ethEnabled;
//         }
// }

export const loadToken = async (web3, networkId, dispatch) => {
    try {
        const token = new web3.eth.Contract(Token.abi, Token.networks[networkId].address) 
        dispatch(tokenLoaded(token))
        return token
    } catch(error) {
      console.log('Contract not deployed to the current network. Please select another network with Metamask.')
      return null  
    }        
}


export const loadExchange = async (web3, networkId, dispatch) => {
    try {
        const exchange = new web3.eth.Contract(Exchange.abi, Exchange.networks[networkId].address) 
        dispatch(exchangeLoaded(exchange))
        return exchange
    } catch(error) {
      console.log('Contract not deployed to the current network. Please select another network with Metamask.')
      return null  
    }        
}


export const loadAllOrders = async(exchange, dispatch) => {      // Fetches all of the Smart Contract stream - All Events
// Fetch cancelled orders with "Cancel" event stream 
    const cancelStream = await exchange.getPastEvents('Cancel', { fromBlock: 0, toBlock: 'latest' }) // setting to 'latest' will check entire blockchain 

// Format cancelled orders
    const cancelledOrders = cancelStream.map((event) => event.returnValues)

// Add cancelled orders to the redux store
    dispatch(cancelledOrdersLoaded(cancelledOrders)) // adding to the Redux state

// Fetch filled orders with the "trade" event stream
    const tradeStream = await exchange.getPastEvents('Trade', { fromBlock: 0, toBlock: 'latest' })

// Format filled orders
    const filledOrders = tradeStream.map((event) => event.returnValues)

// Add trade orders to the redux store
    dispatch(filledOrdersLoaded(filledOrders)) // adding to the Redux state

// Load order stream -- shows all Orders from Exchange.sol
    const orderStream = await exchange.getPastEvents('Order', { fromBlock: 0, toBlock: 'latest' })

// Format order stream
    const allOrders = orderStream.map((event) => event.returnValues)

// Add trade orders to the redux store
    dispatch(allOrdersLoaded(allOrders)) // adding to the Redux state
 }


 // EVENTS
export const subscribeToEvents = async (exchange, dispatch) => {
    exchange.events.Cancel({}, (error, event) => {
        dispatch(orderCancelled(event.returnValues))  
    })

    exchange.events.Trade({}, (error, event) => {
        dispatch(orderFilled(event.returnValues))  
    })

    exchange.events.Deposit({}, (error, event) => {
        dispatch(balancesLoaded())  
    })

   exchange.events.Withdraw({}, (error, event) => {
        dispatch(balancesLoaded())  
    })   
}


export const cancelOrder = (dispatch, exchange, order, account) => {
// Cancel Order and trigger MetaMask    
    exchange.methods.cancelOrder(order.id).send({ from: account })    // access exchange in the Redux state
    .on('transactionHash', (hash) => {                               // on the Client side interaction so use on 
        dispatch(orderCancelling())               // Cancelling order in progress, attempting to cancel the order      
    })   
    .on('error', (error) => {
        console.log(error)
        window.alert('There was an error!')
    })          
 }


export const fillOrder = (dispatch, exchange, order, account) => {
        exchange.methods.fillOrder(order.id).send({ from: account })    // access exchange in the Redux state
        .on('transactionHash', (hash) => {                               // on the Client side interaction so use on 
            dispatch(orderFilling())                  
        })   
        .on('error', (error) => {
            console.log(error)
            window.alert('There was an error!')
        })          
     }


export const loadBalances = async (dispatch, web3, exchange, token, account) => {
        if(typeof account !== 'undefined') {
            // Ether balance in wallet
            const etherBalance = await web3.eth.getBalance(account)
            dispatch(etherBalanceLoaded(etherBalance))
      
            // Token balance in wallet
            const tokenBalance = await token.methods.balanceOf(account).call()
            dispatch(tokenBalanceLoaded(tokenBalance))
      
            // Ether balance in exchange
            const exchangeEtherBalance = await exchange.methods.balanceOf(ETHER_ADDRESS, account).call()
            dispatch(exchangeEtherBalanceLoaded(exchangeEtherBalance))
      
            // Token balance in exchange
            const exchangeTokenBalance = await exchange.methods.balanceOf(token.options.address, account).call()
            dispatch(exchangeTokenBalanceLoaded(exchangeTokenBalance))
      
            // Trigger all balances loaded
            dispatch(balancesLoaded())
          } else {
            window.alert('Please login with MetaMask')
          }
}


export const depositEther = (dispatch, exchange, web3, amount, account) => {
    exchange.methods.depositEther().send({ from: account,  value: web3.utils.toWei(amount, 'ether') })
    .on('transactionHash', (hash) => {
      dispatch(balancesLoading())
    })
    .on('error',(error) => {
      console.error(error)
      window.alert(`There was an error!`)
    })
  }


export const withdrawEther = (dispatch, exchange, web3, amount, account) => {
    exchange.methods.withdrawEther(web3.utils.toWei(amount, 'ether')).send({ from: account })
    .on('transactionHash', (hash) => {
      dispatch(balancesLoading())
    })
    .on('error',(error) => {
      console.error(error)
      window.alert(`There was an error!`)
    })
  }


export const depositToken = (dispatch, exchange, web3, token, amount, account) => {
    amount = web3.utils.toWei(amount, 'ether')
    
    token.methods.approve(exchange.options.address, amount).send({ from: account })     // access exchange in the Redux state
    .on('transactionHash', (hash) => {                                                  // on the Client side interaction so use on 
        exchange.methods.depositToken(token.options.address, amount).send({ from: account })
        .on('transactionHash', (hash) => {
            dispatch(balancesLoading())
            })  
        .on('error', (error) => {
            console.error(error)
            window.alert('There was an error!')
        })
    })          
}


export const withdrawToken = (dispatch, exchange, web3, token, amount, account) => {
    exchange.methods.withdrawToken(token.options.address, web3.utils.toWei(amount, 'ether')).send({ from: account })
    .on('transactionHash', (hash) => {
        dispatch(balancesLoading())
    })
    .on('error', (error) => {
        console.error(error)
        window.alert('There was an error!')
    })
}


export const makeBuyOrder = (dispatch, exchange, token, web3, order, account) => {          // Always a Buy Orer if getting Ether and Tokens
    const tokenGet = token.options.address
    const amountGet = web3.utils.toWei(order.amount, 'ether')
    const tokenGive = ETHER_ADDRESS
    const amountGive = web3.utils.toWei((order.amount * order.price).toString(), 'ether')

    exchange.methods.makeOrder(tokenGet, amountGet, tokenGive, amountGive).send({ from: account })
    .on('transactionHash', (hash) => {
        dispatch(buyOrderMaking())
    })
    .on('error',(error) => {
        console.error(error)
        window.alert('There was an error!')
    })
}



export const makeSellOrder = (dispatch, exchange, token, web3, order, account) => {          // 
    const tokenGet = ETHER_ADDRESS
    const amountGet = web3.utils.toWei((order.amount * order.price).toString(), 'ether')
    const tokenGive = token.options.address
    const amountGive = web3.utils.toWei(order.amount, 'ether')

    exchange.methods.makeOrder(tokenGet, amountGet, tokenGive, amountGive).send({ from: account })
    .on('transactionHash', (hash) => {
        dispatch(sellOrderMaking())
    })
    .on('error',(error) => {
        console.error(error)
        window.alert('There was an error!')
    })
}
