import React, { Component } from 'react'
import { connect } from 'react-redux'
import { OverlayTrigger, Tooltip } from 'react-bootstrap'
import Spinner from './Spinner'
import { 
    orderBookSelector,
    orderBookLoadedSelector,
    exchangeSelector,
    accountSelector,
} from '../store/selectors'
import { fillOrder } from '../interactions'

const renderOrder = (order) => {
    return(
      <OverlayTrigger
        key={order.id}  
        placement="auto"
            overlay={
            <Tooltip id={order.id}>
              {`Click here to ${order.orderFillAction}`}
            </Tooltip>
          }
        >
          <tr 
            key={order.id}
            className="order-book-order"
            onClick={(e) => fillOrder(dispatch, exchange, order, account)}
            >

            <td>{order.tokenAmount}</td>
            <td className={`text-${order.orderTypeClass}`}>{order.tokenPrice}</td>
            <td>{order.etherAmount}</td>
        </tr>
      </OverlayTrigger>
    )
}

const showOrderBook = (props) => {
    const { orderBook } = props

    return(
        <tbody>
            {orderBook.sellOrders.map((order) => renderOrder(order))}
            <tr>
                <th>DAPP</th>
                <th>DAPP/ETH</th>
                <th>ETH</th>
            </tr>            
            {orderBook.buyOrders.map((order) => renderOrder(order))}
        </tbody>
    ) 
}

class OrderBook extends Component {
    render() {
        return (
          <div className="vertical">
            <div className="card bg-dark text-white">
              <div className="card-header">
                Order Book
              </div>
              <div className="card-body">
                <table className="table table-dark table-sm small">
                  { this.props.showOrderBook ? showOrderBook(this.props) : <Spinner type='table' /> }
                </table>
              </div>
            </div>
          </div>
          )
        }
    }
                

function mapStateToProps(state) {
  return {
      orderBook: orderBookSelector(state),
      showOrderBook: orderBookLoadedSelector(state),
      exchange: exchangeSelector(state),
      account: accountSelector(state)
    }
}

export default connect(mapStateToProps)(OrderBook);
