import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Tabs, Tab } from 'react-redux'
import Spinner from './Spinner'
import { loadBalances } from '../store/interactions'
import {
    web3Selector,
    exchangeSelector,
    tokenSelector,
    accountSelector,
    etherBalanceSelector,
    tokenBalanceSelector,
    exchangeTokenBalanceSelector,
    exchangeEtherBalanceSelector,
    balancesLoadingSelector
    } from '../store/selectors'

const showForm =(props) => {
    return(
        <Tabs defaultActiveKey="deposit" className="bg-dark test-white">

            <Tab eventKey="deposit" title="Deposit" className="bg-dark">
            
            </Tab>
            
            <Tab eventKey="withdraw" title="Withdraw" className="bg-dark">

            </Tab>            
        </Tabs>
    )
}


class Balance extends Component {
    componentWillMount() {   
          this.loadBlockchainData()
        }
      
    async loadBlockchainData() {
        const { dispatch, web3, exchange, token, account } = this.props
        await loadBalances(dispatch, web3, exchange, token, account) // Ether balance comes from web3
    }
      
    render() {
        return (
            <div className="card bg-dark text-white">
                <div className="card-header">
                    Balance
                </div>
                <div className="card-body">
                    {this.props.showForm ? showForm(this.props) : <Spinner />}
                </div>
            </div>
        )
    }
}

function mapStateToProps(state) {
    const balancesLoading = balancesLoadingSelector(state)


    // console.log({
    //     account: accountSelector(state),
    //     exchange: exchangeSelector(state),
    //     token: tokenSelector(state),
    //     web3: web3Selector(state),
    //     etherBalance: etherBalanceSelector(state),
    //     tokenBalance: tokenBalanceSelector(state),
    //     exchangeEtherBalance: exchangeEtherBalanceSelector(state),
    //     exchangeTokenBalance: exchangeTokenBalanceSelector(state),
    //     balancesLoading: balancesLoadingSelector(state),
    // })

    return {
        account: accountSelector(state),
        exchange: exchangeSelector(state),
        token: tokenSelector(state),
        web3: web3Selector(state),
        etherBalance: etherBalanceSelector(state),
        tokenBalance: tokenBalanceSelector(state),
        exchangeEtherBalance: exchangeEtherBalanceSelector(state),
        exchangeTokenBalance: exchangeTokenBalanceSelector(state),
        balancesLoading,
        showForm: !balancesLoading
     }
  }    
    
export default connect(mapStateToProps)(Balance)
